import styled from 'styled-components';
import { base1, black } from './colours';

const Title1 = styled.h1`
  font-size: 4.375rem;
  font-family: 'Canela';
  margin: 0;
`;

const Text1 = styled.p`
  position: absolute;
  width: 632px;
  height: 493px;
  left: 166px;
  top: 376px;

  font-family: JostBook;
  font-style: normal;
  font-weight: normal;
  font-size: 20px;
  line-height: 29px;

  color: #000000;
`;

const Button = styled.button`
  background: ${base1};
  color: ${black};
  font-size: 1em;
  margin: 1em;
  padding: 0.25em 1em;
  font-family: 'JostBook';
  border-radius: 3px;
`;

export { Title1, Text1, Button };
