import React from 'react';
import styled from 'styled-components';

const Nav = styled.nav`
  display: flex;
  justify-content: space-between;
  height: 100px;
`;

const StyledUL = styled.ul`
  display: flex;
  justify-content: space-evenly;
  marign: auto 0 auto 0;
`;

const StyledLink = styled.a`
  font-family: Jost *;
  font-style: normal;
  font-weight: normal;
  font-size: 22px;
  line-height: 32px;
  /* identical to box height */

  display: flex;
  align-items: center;
  text-align: center;
`;

const StyledLI = styled.li`
  margin: auto 60px auto 0;
  list-style-type: none;
`;

const NameLink = styled.a`
  margin: auto 0 auto 10%;
  font-family: Canela;
  font-style: normal;
  font-weight: normal;
  font-size: 45px;
  line-height: 45px;

  display: flex;
  align-items: center;
  text-align: center;
`;

const navItems = [
  {
    title: 'home',
    titleHref: '/',
  },
  {
    title: 'about',
    titleHref: '/about',
  },
  {
    title: 'projects',
    titleHref: '/projects',
  },
  {
    title: 'contact',
    titleHref: '/contact',
  },
];

export default function NavBar({ currentPage, setCurrentPage }) {
  return (
    <Nav>
      <NameLink>James Hardy</NameLink>
      <StyledUL>
        {navItems.map((item) => {
          return (
            <StyledLI
              key={item.title}
              to={item.titleHref}
              onClick={() => setCurrentPage(item.title)}
            >
              <StyledLink> {item.title}</StyledLink>
            </StyledLI>
          );
        })}
      </StyledUL>
    </Nav>
  );
}
