import React from 'react';
import styled from 'styled-components';
import { Github, Linkedin } from '@styled-icons/boxicons-logos';

const StyledAside = styled.aside`
  display: flex;
  flex-direction: column;
  align-items: space-evenly;
  margin
`;

const Button = styled.a`
  display: flex;
  justify-content: space-between;
  padding: 0.5rem 0.5rem;
  margin: 0.5rem 10rem;
  width: 9rem;
  background: transparent;
  color: black;
  text-decoration: none;
  background: ${(props) => props.colour};
  color: ${(props) => props.innerColour};
  font-family: Jost *;
  font-style: normal;
  font-weight: 300;
  font-size: 22px;
  line-height: 32px;
  text-transform: capitalize;
  transform: rotate(-90deg);

  &:hover {
    box-shadow: 4px 4px rgba(33, 33, 33, 0.2);
    transition: box-shadow 0.3s ease-in-out;
  }
  &:hover::after {
    // transition: box-shadow 0.3s ease-in-out;
  }
`;

const InnerText = styled.p``;

const StyledGit = styled(Github)`
  height: 25px;
  width: 25px;
  margin: 3px 0 0 0;
  transform: rotate(90deg);
`;

const StyledLI = styled(Linkedin)`
  height: 25px;
  width: 25px;
  margin: 3px 0 0 0;
  transform: rotate(90deg);
`;

const links = [
  {
    title: 'github',
    href: 'https://www.github.com',
    colour: ' #2D4E35 ',
    innerColour: '#FFFFFF',
  },
  {
    title: 'linkedin',
    href: 'https://www.linkedin.com',
    colour: '#E9ECE6 ',
    innerColour: '#000000',
  },
];

export default function AsideButtons() {
  return (
    <StyledAside>
      {links.map((item) => (
        <Button
          colour={item.colour}
          innerColour={item.innerColour}
          href={item.href}
        >
          <InnerText>{item.title}</InnerText>
          {item.title == 'github' ? <StyledGit /> : <StyledLI />}
        </Button>
      ))}
    </StyledAside>
  );
}
