import styled from 'styled-components';
import { base1, black } from './colours';

const Title1 = styled.h1`
  position: absolute;
  width: 362px;
  height: 70px;
  left: 166px;
  top: 266px;

  font-family: Canela;
  font-style: normal;
  font-weight: normal;
  font-size: 70px;
  line-height: 70px;
  /* identical to box height */

  display: flex;
  align-items: center;
  text-transform: capitalize;

  color: #ffffff;
`;

const Text1 = styled.p`
  font-size: 1.25rem;
  font-family: 'JostBook';
  margin: 0;
`;

const Button = styled.button`
  background: ${base1};
  color: ${black};
  font-size: 1em;
  margin: 1em;
  padding: 0.25em 1em;
  font-family: 'JostBook';
  border-radius: 3px;
`;

export { Title1, Text1, Button };
