import React from 'react';
import headShotImage from '../Assets/16200025.JPG';
import styled from 'styled-components';
import AsideButtons from '../AsideButtons';

const ContainerMain = styled.article`
  display: flex;
  justify-content: space-between;
  height: 100vh;
  margin: 5rem 0 0 0;
`;
const Container = styled.article`
  flex-grow: 1;
`;

const StyledHeader = styled.header`
  display: flex;
  justify-content: space-between;
  margin: 0 0 3rem 0;
  width: 100vw;
`;

const PageTitle = styled.h1`
  margin: auto 0 auto 10%;
  font-family: Canela;
  font-style: normal;
  font-weight: normal;
  font-size: 70px;
  line-height: 70px;
  display: flex;
  align-items: center;
  text-transform: capitalize;
`;

const ReturnButton = styled.button`
  height: 20px;
  width: 20px;
  border-radius: 50%;
  margin: auto 30% auto 0;
  border: none;

  background: #f2ddcd;
  box-shadow: 4px 4px 1px #8b1e10;

  &:hover {
    transform: translate(0%, -30%);
    transition: 0.5s ease-out;
  }
`;

const Section = styled.section`
  display: flex;
`;

const Text = styled.p`
  flex-grow: 0.5;
  text-align: right;
  margin: 0 5px 0 17%;
  width: 188px;
`;

const LandingImg = styled.img`
  flex-grow: 1;
  width: 30vw;
  height: 50vh;
  object-fit: cover;
  object-position: 40% 0;
`;
const StyledH2 = styled.h2`
  margin: 0 auto 0 auto;
  font-family: Canela;
  font-style: normal;
  font-weight: normal;
  font-size: 50px;
  display: flex;
  align-items: center;
  text-transform: capitalize;
`;

const FlexDiv = styled.div`
  flex-grow: 4;
  margin: 0 0 0 0;
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
  height: 33rem;
`;

const StyledSpan = styled.span`
  font-family: Jost *;
  font-style: normal;
  font-weight: 300;
  font-size: 50px;
  /* identical to box height */
  margin: 0 auto 0 auto;

  display: flex;
  align-items: center;
  text-align: center;
`;

const StyledAside = styled.aside`
  flex-grow: 1;
`;

export default function Home() {
  return (
    <ContainerMain>
      <Container>
        <StyledHeader>
          <PageTitle>my portfolio</PageTitle>
          <ReturnButton />
        </StyledHeader>
        <Section>
          <Text>
            Iâ€™m a Full Stack Developer with a background in Music and Teaching.
            I am passionate about social impact projects and working with new
            technologies. I love electronic music, cooking and cats.
          </Text>
          <LandingImg src={headShotImage} alt="Image of James" />
          <FlexDiv>
            <StyledH2>FullStack</StyledH2>
            <StyledSpan>Developer</StyledSpan>
          </FlexDiv>
        </Section>
      </Container>
      <AsideButtons />
    </ContainerMain>
  );
}
