import styled from 'styled-components';

const Title1 = styled.h1`
  position: absolute;
  width: 362px;
  height: 70px;
  left: 166px;
  top: 266px;

  font-family: Canela;
  font-style: normal;
  font-weight: normal;
  font-size: 70px;
  line-height: 70px;

  display: flex;
  align-items: center;
  text-transform: capitalize;

  color: #ffffff;
`;

const Title2 = styled.h1`
  font-size: 3.75rem;
  font-family: 'JostLight';
  margin: 0;
`;
const Title3 = styled.h1`
  font-size: 3.75rem;
  font-family: 'JostLight';
  margin: 0;
`;

const Text1 = styled.p`
  font-size: 1.25rem;
  font-family: 'JostBook';
  margin: 0;
`;

const Img = `
max-width: 50vw;
`;

//FIX MEDIA QUERIES
const LandingImg = styled.img`
  ${Img}
  max-width: 50rem;
  @media (max-width: 414px) {
    max-width: 20rem;
  }
`;

export { Title1, Title2, Title3, Text1, LandingImg };
