import React from 'react';
import headShotImage from '../Assets/16200025.JPG';
import styled from 'styled-components';

const Container = styled.article``;

const StyledHeader = styled.header`
  display: flex;
  justify-content: space-between;
`;

const PageTitle = styled.h1`
  margin: auto 0 auto 10%;
  font-family: Canela;
  font-style: normal;
  font-weight: normal;
  font-size: 70px;
  line-height: 70px;
  display: flex;
  align-items: center;
  text-transform: capitalize;
`;

const ReturnButton = styled.button`
  height: 20px;
  width: 20px;
  border-radius: 50%;
  margin: auto 30% auto 0;
  border: none;

  background: #f2ddcd;
  box-shadow: 4px 4px 1px #8b1e10;

  &:hover {
    transform: translate(0%, -30%);
    transition: 0.5s ease-out;
  }
`;

const Section = styled.section`
  display: flex;
`;

const Text = styled.p`
  margin: 0 0 auto 10%;
  text-align: right;

  width: 231px;
`;

const LandingImg = styled.img`
  width: 365px;
  height: 453px;
  margin-right: 30%;
  object-fit: cover;
  object-position: 40% 0;
`;
const StyledH2 = styled.h2``;

const FlexDiv = styled.div``;

export default function Home() {
  return (
    <Container>
      <StyledHeader>
        <PageTitle>my portfolio</PageTitle>
        <ReturnButton />
      </StyledHeader>
      <Section>
        <Text>
          Iâ€™m a Full Stack Developer with a background in Music and Teaching. I
          am passionate about social impact projects and working with new
          technologies. I love electronic music, cooking and cats.
        </Text>
        <LandingImg src={headShotImage} alt="Image of James" />
        <FlexDiv>
          <styledH2></styledH2>
        </FlexDiv>
      </Section>
    </Container>
  );
}
